function checkEmail(a){var e=/(^[a-z]([a-z_\.]*)@([a-z_\.]*)([.][a-z]{3})$)|(^[a-z]([a-z_\.]*)@([a-z_\.]*)(\.[a-z]{3})(\.[a-z]{2})*$)/i;return e.test(a)}$(document).ready(function(){$("#invite_writer").click(function(){var a=document.getElementById("email_text").value,e=a.split(","),t="";for(i=0;i<=e.length-1;i++)checkEmail(e[i])||(t+=e[i]+"\n");return""!=t?(alert("You have Invalid Emails:\n"+t),$("#email_text").focus(),!1):void 0})});